/* eslint-disable default-case */
import React from "react";
import { Carousel } from "../Carousel/Carousel";
import { Directions } from "../Directions/Directions";
import { PricesAndConditions } from "../PricesAndConditions/PricesAndConditions";

export const Main = ({ isMenuOpened, language }) => {

    const mainDescriptionLanguage = () => {
        const descriptions = {
            italian:
                'Il <span className="highlight">B&B Favignana</span>, incastonato nel cuore storico di un incantevole paesino, rappresenta una destinazione perfetta per chi cerca una vacanza all\'insegna del relax e della bellezza naturale. La vicinanza alla spiaggia <span className="highlight">La Playa</span> lo rende ideale per le famiglie, mentre la varietà di servizi disponibili garantisce comodità e divertimento per tutti i visitatori. L\'isola di Favignana, gioiello delle Egadi, offre scenari mozzafiato e un mare cristallino da esplorare, oltre a una vivace vita sociale per i più giovani. La tranquillità estiva del centro pedonale arricchisce l\'esperienza, permettendo di immergersi completamente nella serenità dell\'isola.',
            english: 'English description',
        };

        return <p className="location-description">{descriptions[language]}</p>;
    };
    return (
        <main className={!isMenuOpened ? "main" : "hidden"}>

            <div className="text-location-container">
                <h4 className="description-title">Il Tuo Rifugio Perfetto nel Cuore di Favignana</h4>
                {mainDescriptionLanguage()}
            </div>


            <Carousel />
            <span className="line"></span>
            <Directions isMenuOpened={isMenuOpened} />
            <PricesAndConditions isMenuOpened={isMenuOpened} language={language} />
            <span className="line"></span>
        </main>
    );
};
