import React from 'react';
import ROOMS_JSON from './../../JSON/room-images.json';

export const PhotoCarousel = () => {


  const mapRooms = () => {

    const MAX_IMAGES = 7;
    const randomImageIndex = Math.floor(Math.random()  * MAX_IMAGES);
    const imagePath = process.env.PUBLIC_URL + ROOMS_JSON.find(room => room.name === 'Foto Carosello').images[randomImageIndex];
    return ROOMS_JSON.map((room, index) => {
      return (
        <div key={index} className="carousel-item">
          <img className="d-block w-100" src={imagePath} alt={room.name} />
        </div>
      );
    });
  };


  return (
    {mapRooms}
  );
}
