import React from 'react';
import ROOMS_JSON from './../../JSON/room-images.json';

export const PhotoCarousel = () => {


  const mapRooms = () => {
    const randomImageIndex = Math.floor(Math.random() * ROOMS_JSON.find((room) => room.images.length).images.length); // random index between 0 and number of images
    const imagePath = process.env.PUBLIC_URL + randomImageIndex;

    return ROOMS_JSON.map((room, index) => {

      return (
        <div key={index} className="carousel-item">
          <img className="d-block w-100" src={imagePath} alt={room.name} />
        </div>
      );
    });
  };


  return (
    <div>
      {mapRooms()}
    </div>
  );
}
