import React from "react";
import { Link } from "react-router-dom";
import { Menu } from "../Menu/Menu";

export const Header = (
    isMenuOpened,
    setIsMenuOpened,
    handleMenuClick,
) => {

    return (
        <div className="header">
            <div className="wrap-header">
                <Link to={'/'} className="logo-link">
                    <img className="bnb-logo-img" alt="bnb Favignana Logo" src={process.env.PUBLIC_URL + '/images/logo/bnb-logo.png'}></img>
                </Link>

                <div className="place-name-container">
                    <h1 className="place-name">B&B Favignana</h1>
                </div>

            </div>
            <menu className="wrap-menu">
                <Menu
                    handleMenuClick={handleMenuClick}
                    setIsMenuOpened={setIsMenuOpened}
                    isMenuOpened={isMenuOpened}
                />
            </menu>
        </div>
    )
}
