import React from "react";

export const Directions = ({ isMenuOpened }) => {

    const publicPath = process.env.PUBLIC_URL;
    const servicesList = [
        { distance: "30m", image: publicPath + "/public/images/services/boat.svg"  },
        { distance: "90m", image: "https://via.placeholder.com/150" },
        { distance: "180m", image: "https://via.placeholder.com/150" },
        { distance: "100m", image: "https://via.placeholder.com/150" },
    ];

    return (
        <div className={isMenuOpened ? "hidden" : "directions"}>
            <div className="directions-container">
                <h2 className="directions-title">Dove siamo</h2>
                <p className="directions-description">
                    Il nostro Bed & Breakfast Ã¨ un'oasi di comfort situata <b>Via Roma 15, Favignana</b>.
                </p>
            </div>

            <div className="near-locations-container">
                <h2 className="services-title">Servizi Vicini</h2>
                <ul className="services-list">
                    {servicesList.map((item, index) => (
                        <li key={index} className="service-item">
                            <p className="service-type-image">{item.service}</p>
                            <p className="service-distance">{item.distance}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};
