import React from "react";
import { bookingPeriods } from "../../data";

export const PricesAndConditions = ({
    language,
    isMenuOpened,
}) => {
    const periods = bookingPeriods[language];

    return (
        <div className={isMenuOpened ? "hidden" : "prices-and-conditions"}>
            <p className="title">{language === 'italian' ? 'PREZZI E CONDIZIONI' : 'PRICES AND CONDITIONS'}</p>
            <div className="prices-table">
                {periods.map(({ name, date, prices }, index) => (
                    <div className="section" key={index}>
                        <h4 className="section-title">{name}</h4>
                        <div className="wrap-dates">
                            <p className="first-date">{date?.[0] || ""}</p>
                            <p className="second-date">{date?.[1] || ""}</p>
                        </div>
                        <div className="wrap-prices">
                            <p className="room-price">{Object.keys(prices)[0]}: <b>{prices?.[Object.keys(prices)[0]] || ""}</b></p>
                            <p className="room-price">{Object.keys(prices)[1]}: <b>{prices?.[Object.keys(prices)[1]] || ""}</b></p>
                            <p className="room-price">{Object.keys(prices)[2]}: <b>{prices?.[Object.keys(prices)[2]] || ""}</b></p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
